JW_APP_NAME.directive("trackWidth", trackWidth); function trackWidth(){return{scope:{trackWidth:"=", minWidth:"="}, link:function(b, a){b.trackWidth = $(a).width() < b.minWidth; b.prevTrackWidth = b.trackWidth; $(window).resize(function(){var c = $(a).width() < b.minWidth; if (c !== b.prevTrackWidth){b.$apply(function(){b.trackWidth = c})}b.prevTrackWidth = c})}}};